sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

var token = request.headers['x-api-key'];
var expectedToken = "nfh23jkd9281##AJSNkxas213snas!@#1234";

if (!token || token !== expectedToken) {
    response.setStatus(403);
    response.setBody("Unauthorized: Invalid or missing API key.");
    return;
}

    var parsed = JSON.parse(request.body.data);
    var reqNum = parsed.requestNumber;

    var gr = new GlideRecord('New Employee Access Request'); 
    gr.addQuery('number', reqNum);
    gr.query();

    if (gr.next()) {
        // Map incoming data to the actual fields
        gr.deactivation_status = parsed.status;               // e.g., "Deactivated"
        gr.okta_comment = parsed.comments;


        gr.update();

        response.setStatus(200);
        response.setBody("Request " + reqNum + " updated successfully.");
    } else {
        response.setStatus(404);
        response.setBody("Request not found for number: " + reqNum);
    }

})(request, response);
